  `ip` varchar(16) NOT NULL DEFAULT '' COMMENT '操作人IP',
  PRIMARY KEY (`account_apply_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='入账申请表';

