-- ----------------------------
--  Table structure for `tp_item`
-- ----------------------------
DROP TABLE IF EXISTS `tp_item`;
CREATE TABLE `tp_item` (
  `item_id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '自增主键',
  `item_name` varchar(255) NOT NULL DEFAULT '' COMMENT '商品名称',
  `item_desc` varchar(255) NOT NULL DEFAULT '商品简介' COMMENT '商品简介',
  `unit` varchar(8) NOT NULL COMMENT '单位',
  `item_sn` varchar(32) NOT NULL DEFAULT '' COMMENT '商品货号',
  `has_sku` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否包含尺寸规格（SKU），1有，0无，默认0',
  `is_gift` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '是否礼品，1是，0否，礼品则只出现在积分兑换专区，即只能用积分全额购买',
  `item_type_id` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '商品类型ID',
  `base_pic` varchar(128) NOT NULL DEFAULT '' COMMENT '原图',
  `mall_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '店铺价',
  `class_id` smallint(6) NOT NULL DEFAULT '0' COMMENT '一级分类ID',
  `sort_id` smallint(6) NOT NULL DEFAULT '0' COMMENT '二级分类ID',
  `stock` int(11) NOT NULL DEFAULT '0' COMMENT '库存',
  `sales_num` int(11) NOT NULL DEFAULT '0' COMMENT '销售量',
  `clickdot` int(11) NOT NULL DEFAULT '0' COMMENT '点击量',
  `addtime` int(11) NOT NULL DEFAULT '0' COMMENT '添加时间',
  `onstore_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '上架时间',
  `serial` tinyint(4) NOT NULL DEFAULT '0' COMMENT '排序号',
  `default_serial` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '综合排序，综合商品销量、商家好评率、上架时间、价格排序',
  `isuse` tinyint(4) NOT NULL DEFAULT '0' COMMENT '3种含义，0下架，1上架，2已删除（假删除）',
  `purchase_limit` tinyint(3) unsigned DEFAULT '0' COMMENT '限购数量，0表示不限制',
  `brand_id` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '品牌id',
  `integral_exchange_rate` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '积分抵扣比例',
  `stock_alarm` int(11) NOT NULL DEFAULT '0' COMMENT '警戒库存',
  `weight` int(11) NOT NULL DEFAULT '0' COMMENT '重量，单位g',
  `market_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '市场价',
  `goods_code` varchar(32) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL DEFAULT '7000' COMMENT '商品编码',
  `is_real` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否实物',
  `is_recommend` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否首页显示',
  `use_hero` varchar(32) NOT NULL DEFAULT '' COMMENT '使用英雄',
  PRIMARY KEY (`item_id`)
) ENGINE=MyISAM AUTO_INCREMENT=56 DEFAULT CHARSET=utf8 COMMENT='商品表';

