  `item_package_id` int(11) NOT NULL DEFAULT '0' COMMENT '订单ID',
  `item_id` int(11) NOT NULL DEFAULT '0' COMMENT '商品ID',
  `number` int(11) NOT NULL DEFAULT '0' COMMENT '商品数量',
  PRIMARY KEY (`item_package_detail_id`),
  KEY `tp_item_package` (`item_package_id`),
  KEY `tp_item` (`item_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='套餐-商品关联表';

