-- ----------------------------
--  Table structure for `tp_item_refund_change`
-- ----------------------------
DROP TABLE IF EXISTS `tp_item_refund_change`;
CREATE TABLE `tp_item_refund_change` (
  `item_refund_change_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增主键',
  `operate_type` tinyint(4) NOT NULL DEFAULT '0' COMMENT '操作类型，1退货，2换货',
  `order_id` int(11) NOT NULL DEFAULT '0' COMMENT '订单ID',
  `refund_type` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '退款原因，1未收到货，2超时，3质量问题，4收到的商品数量与购买的数量不一致，5镖师态度问题，6其他原因',
  `item_ids` varchar(255) NOT NULL DEFAULT '' COMMENT '订单商品ID列表，多个订单商品ID用英文逗号隔开',
  `item_nums` varchar(255) NOT NULL DEFAULT '' COMMENT '退款商品件数，必须和item_ids对应',
  `complain_user_ids` varchar(255) NOT NULL DEFAULT '' COMMENT '投诉对象ID列表，英文逗号隔开',
  `refund_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '退还金额，若为负数，需要代理商方面补偿金额，由双方协商通过其他途径支付',
  `reason` text COMMENT '退换货原因描述',
  `proof` varchar(128) NOT NULL DEFAULT '' COMMENT '退换货凭证，照片',
  `express_number` varchar(32) NOT NULL DEFAULT '' COMMENT '物流单号',
  `state` tinyint(4) NOT NULL DEFAULT '0' COMMENT '退换货状态，0未处理，1通过，2拒绝',
  `addtime` int(11) NOT NULL DEFAULT '0' COMMENT '添加时间',
  `handle_time` int(11) NOT NULL DEFAULT '0' COMMENT '处理时间',
  `admin_remark` varchar(255) NOT NULL DEFAULT '' COMMENT '管理员备注',
  `isuse` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否启用，1启用，0关闭',
  PRIMARY KEY (`item_refund_change_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='退换货申请表';

-- ----------------------------
--  Table structure for `tp_item_sku_price`
-- ----------------------------
DROP TABLE IF EXISTS `tp_item_sku_price`;
CREATE TABLE `tp_item_sku_price` (
  `item_sku_price_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增主键',
  `item_id` int(11) NOT NULL DEFAULT '0' COMMENT '商品ID',
  `item_sn` varchar(255) NOT NULL DEFAULT '0' COMMENT '商品货号',
  `sku_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT 'SKU商品价格',
  `sku_stock` int(11) NOT NULL DEFAULT '0' COMMENT 'SKU商品库存',
  `serial` tinyint(4) NOT NULL DEFAULT '0' COMMENT '排序号',
  `property_value1` int(11) NOT NULL DEFAULT '0' COMMENT '规格值ID，商品最多允许有两种规格，此处为第一种，可不填',
  `property_value2` int(11) NOT NULL DEFAULT '0' COMMENT '规格值ID，商品最多允许有两种规格，此处为第二种，可不填',
  `isuse` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否启用，1启用，0关闭',
  `goods_code` varchar(32) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL DEFAULT '7000' COMMENT '商品编码',
  PRIMARY KEY (`item_sku_price_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='商品SKU价格关联表';

-- ----------------------------
--  Table structure for `tp_item_txt`
-- ----------------------------
DROP TABLE IF EXISTS `tp_item_txt`;
CREATE TABLE `tp_item_txt` (
  `item_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `contents` text COMMENT '商品详情描述',
  PRIMARY KEY (`item_id`)
) ENGINE=MyISAM AUTO_INCREMENT=56 DEFAULT CHARSET=utf8 COMMENT='商品详情描述表';

